import 'bootstrap/dist/css/bootstrap.min.css';
import { Fragment, useState } from 'react';

const EditTodo =(todo) =>
{
    const [descriptionEdit, setDescriptionEdit] = useState("");

    const handleEdit = async (e, id) =>
    {
        e.preventDefault();

        try {
            console.log(id)
            const response = await fetch(`http://localhost:5000/todos/${id}`,
            {method : "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                description: descriptionEdit // Assuming descriptionEdit is the new description value
            })
        })
        }
        catch (err) {
            console.err(err);
        }
    }
    return (
            

        <Fragment>
            <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#myModal">Edit</button>
            <div class="modal" id="myModal">
                <div class="modal-dialog">
                    <div class="modal-content">

                    <div class="modal-header">
                        <h4 class="modal-title"> {todo.description}</h4>
                        <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
                    </div>

                    <div class="modal-body">
                        <form className='form-control d-flex' onSubmit={(e) => {
                            console.log(todo.todo_id)
                            handleEdit(e, todo.todo_id)}}>
                            <input type="text" className='me-1' onChange={(e) => setDescriptionEdit(e.target.value)}/>
                            <button className='btn btn-success' type="submit">Phung Edit</button>
                        </form>
                    </div>

                    <div class="modal-footer">
                        <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Close</button>
                    </div>

                    </div>
                </div>
            </div>
        </Fragment>

    )
}

export default EditTodo;
