import 'bootstrap/dist/css/bootstrap.min.css';
import React, {Fragment, useEffect, useState} from "react";

import EditTodos from './EditTodo';

const ListTodo = () =>
{
    const [todos, setTodos] = useState([]);
    const [descriptionEdit, setDescriptionEdit] = useState("");
    
    const handleEdit = async (e, id) =>
    {

        e.preventDefault();

        try {
            const response = await fetch(`http://localhost:5000/todos/${id}`,
            {method : "PUT"})

            console.log(todos)
            console.log(1)

            const newData = todos.map((currentElement) => {if(currentElement.todo_id == id)
            {
                currentElement.description = descriptionEdit

            }})

            todos = setTodos(newData)
        }
        catch (err) {
            console.err(err);
        }
    }

    //delete a todo
    const deleteTodos = async (id) =>
    {
        try {
            const response = await fetch(`http://localhost:5000/todos/${id}`,
            {
                method: "DELETE"
            })
            setTodos(todos.filter((currentElement) => {if(id !== currentElement.todo_id){return currentElement}}));
        } catch (err) {
            console.log(err);
        }
    }
    const getTodos = async () =>
    {
        try {
            const response = await fetch("http://localhost:5000/todos");
            const jsonData = await response.json();
            setTodos(jsonData);
            
        } catch (err) {
            console.log(err)
        }

    }

    useEffect(() => {
        getTodos();
    }, []);
    
    return (
        <div className="container">
            <table class="table">
                <thead>
                    <tr>
                        <th>Description</th>              
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    todos.map((elementObject) => {return (
                        <tr>
                            <td>{elementObject.description}</td>
                            
                            <EditTodos />
                            <td>
                                <button class="btn btn-warning" onClick={() => deleteTodos(elementObject.todo_id)}>x</button>
                            </td>
                        </tr>
                    )})  
                    }
        
                </tbody>

            </table>
        </div>
    )
}

export default ListTodo;
