import 'bootstrap/dist/css/bootstrap.min.css';
import { Fragment, useState } from 'react';
import "./../assets/style.css"

const EditTodo =({todo}) =>
{
    const [descriptionEdit, setDescriptionEdit] = useState(todo.description);
    const [clickedEdit, setClickEdit] = useState(false);

    const handleEdit = async (e, id) =>
    {
        e.preventDefault();
        console.log(todo.todo_id)
        const body = {descriptionEdit}
        console.log(descriptionEdit)
        try {
            console.log(id)
            const response = await fetch(`http://localhost:5000/todos/${id}`,
            {method : "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                description: descriptionEdit // Assuming descriptionEdit is the new description value
            })
        })
        }

        catch (err) {
            console.err(err);
        }
    }
    return (     
        <Fragment>
            <button className='btn btn-primary' onClick={() => setClickEdit(true)}>Edit</button>
            {clickedEdit && 
            <>
                <div className='modal-custom justify-content-center d-flex' id={`modal${todo}`}>

                    <form action="" className='mt-5' onSubmit={(e) => {

                        handleEdit(e, todo)}}>
                        <input type="text" />
                        <button type='submit' className='btn btn-success' onChange={(e) => setDescriptionEdit(e.target.value)}>Edit</button>
                        <button onClick={()=>setClickEdit(false)}>Close</button>
                    </form>
                </div>
            </>
            } 
        </Fragment>
    )
}

export default EditTodo;
