import 'bootstrap/dist/css/bootstrap.min.css';
import React, {Fragment, useEffect, useState} from "react";


const ListTodo = () =>
{
    const [todos, setTodos] = useState([]);
    const handleChangeEdit = (e) =>
    {
        const value = e.target.value;
    }
    const handleSubmitEdit = async () =>
    {

    }

    const editTodos = async (id) =>
    {
        try {
            const response = await fetch("http://localhost:5000/todos/${id}",
            {
                method : "PUT"
            })
            const restData = todos.filter((currentTodo) => { if(id != currentTodo.todo_id )
            {
                return currentTodo;
            }})
            setTodos(restData);

        } catch (err) {
            console.log(err);
        }
    } 
    //delete todos
    const deleteTodos = async (id) =>
    {
        try {
            const response = await fetch("http://localhost:5000/todos/${id}",
            {
                method : "DELETE"
            })
            const restData = todos.filter((currentTodo) => { if(id != currentTodo.todo_id )
            {
                return currentTodo;
            }})
            setTodos(restData);


        } catch (err) {
            console.log(err);
        }
    }
    const getTodos = async () =>
    {
        try {
            const response = await fetch("http://localhost:5000/todos");
            const jsonData = await response.json();
            setTodos(jsonData.rows);
            
        } catch (err) {
            console.log(err)
        }

    }

    useEffect(() => {
        getTodos();

    }, []);
    
    return (
        <div className="container">
            <table class="table">
                <thead>
                    <tr>
                        <th>Description</th>              
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    todos.map((elementObject) => {return (
                        <tr>
                            <td>{elementObject.description}</td>
                            <td>
                                <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#myModal">Edit</button>
                                <div class="modal" id="myModal">
                                    <div class="modal-dialog">
                                        <div class="modal-content">

                                        <div class="modal-header">
                                            <h4 class="modal-title">Edit</h4>
                                            <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
                                        </div>

                                        <div class="modal-body">
                                            <form className='form-control d-flex' onSubmit={() => handleSubmitEdit()}>
                                                <input type="text" className='me-1' onChange={handleChangeEdit}/>
                                                <button className='btn btn-success'>Edit</button>
                                            </form>
                                        </div>

                                        <div class="modal-footer">
                                            <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Close</button>
                                        </div>

                                        </div>
                                    </div>
                                    </div>
                                </td>
                            <td>
                                <button class="btn btn-warning"  onClick={() => deleteTodos(elementObject.todo_id)}>x</button>
                            </td>
                        </tr>
                    )})  
                    }
        
                </tbody>

            </table>
        </div>
    )
}

export default ListTodo;
