import 'bootstrap/dist/css/bootstrap.min.css';
import React, {Fragment, useEffect, useState} from "react";

import EditTodo from './EditTodo';

const ListTodo = () =>
{
    const [todos, setTodos] = useState([]);

    //delete a todo
    const deleteTodos = async (id) =>
    {
        try {
            const response = await fetch(`http://localhost:5000/todos/${id}`,
            {
                method: "DELETE"
            })
            setTodos(todos.filter((currentElement) => {if(id !== currentElement.todo_id){return currentElement}}));
        } catch (err) {
            console.log(err);
        }
    }

    const getTodos = async () =>
    {
        try {
            const response = await fetch("http://localhost:5000/todos");
            const jsonData = await response.json();
            setTodos(jsonData);
            console.log(jsonData)
            
        } catch (err) {
            console.log(err)
        }
        const EditTodo =({todo, getTodos}) =>
{
    const [description, setDescription] = useState(todo.description);
    const [clickedEdit, setClickEdit] = useState(false);
    const handleEdit = async (e, id) => {
        e.preventDefault();
        const body = { description };
        try {
            const response = await fetch(`http://localhost:5000/todos/${id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(body)
            });
            if (response.ok) {
                // Data has been successfully updated, fetch the updated list of todos

                getTodos();
                window.location.reload()

            } else {
                console.error("Failed to update todo.");
                // Optionally handle the case where the update fails
            }
        } catch (err) {
            console.error(err);
        }
    };
    return (     
        <Fragment>
            <button className='btn btn-primary' onClick={() => setClickEdit(true)}>Edit</button>
            {clickedEdit && 
            <>
                <div className='modal-custom justify-content-center d-flex' id={`modal${todo}`}>

                    <form action="" className='mt-5' onSubmit={(e) => {

                        handleEdit(e, todo.todo_id)}}>
                        <input type="text"  value={description} onChange={(e) => setDescription(e.target.value)}/>
                        <button type='submit' className='btn btn-success'  >Agree</button>
                        <button type='button' onClick={()=>setClickEdit(false)}>Close</button>
                    </form>
                </div>
            </>
            } 

        </Fragment>
    )
}
    }

    useEffect(() => {
        getTodos();
    },[]);
    
    return (
        <div className="container">
            <table class="table">
                <thead>
                    <tr>
                        <th>Description</th>              
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    todos.map((elementObject) => {
                        return (
                        <tr>
                            <td>{elementObject.description}</td>
                            <td>
                                <EditTodo todo={elementObject} getTodos={getTodos}/>
                            </td>
                            <td>
                                <button class="btn btn-warning" onClick={() => deleteTodos(elementObject.todo_id)}>x</button>
                            </td>
                        </tr>
                    )})  
                    }
        
                </tbody>

            </table>
        </div>
    )
}

export default ListTodo;
