import 'bootstrap/dist/css/bootstrap.min.css';
import React, {Fragment, useEffect, useState} from "react";


const ListTodo = () =>
{
    const [todos, setTodos] = useState([]);
    const [descriptionEdit, setDescriptionEdit] = useState("");
    
    const handleEdit = async (e, id) =>
    {
        e.prevenDefault()
        console.log(descriptionEdit)

        try {
            const response = await fetch(`http://localhost:5000/todos/${id}`,
            {method : "PUT"})

            setTodos(todos.map((currentElement) => {if(id == currentElement.todo_id)
            {
                currentElement.description = descriptionEdit
            }}))
        }
        catch (err) {
            console.err(err);
        }
    }

    //delete a todo
    const deleteTodos = async (id) =>
    {
        try {
            const response = await fetch(`http://localhost:5000/todos/${id}`,
            {
                method: "DELETE"
            })
            setTodos(todos.filter((currentElement) => {if(id !== currentElement.todo_id){return currentElement}}));
        } catch (err) {
            console.log(err);
        }
    }
    const getTodos = async () =>
    {
        try {
            const response = await fetch("http://localhost:5000/todos");
            const jsonData = await response.json();
            setTodos(jsonData);
            
        } catch (err) {
            console.log(err)
        }

    }

    useEffect(() => {
        getTodos();
    }, []);
    
    return (
        <div className="container">
            <table class="table">
                <thead>
                    <tr>
                        <th>Description</th>              
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    todos.map((elementObject) => {return (
                        <tr>
                            <td>{elementObject.description}</td>
                            <td>
                                <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#myModal">Edit</button>
                                <div class="modal" id="myModal">
                                    <div class="modal-dialog">
                                        <div class="modal-content">

                                        <div class="modal-header">
                                            <h4 class="modal-title">Edit</h4>
                                            <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
                                        </div>

                                        <div class="modal-body">
                                            <form className='form-control d-flex' onSubmit={(e) => handleEdit(e, elementObject.todo_id)}>
                                                <input type="text" className='me-1' onChange={(e) => setDescriptionEdit(e.target.value)}/>
                                                <button className='btn btn-success'>Edit</button>
                                            </form>
                                        </div>

                                        <div class="modal-footer">
                                            <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Close</button>
                                        </div>

                                        </div>
                                    </div>
                                    </div>
                                </td>
                            <td>
                                <button class="btn btn-warning" onClick={() => deleteTodos(elementObject.todo_id)}>x</button>
                            </td>
                        </tr>
                    )})  
                    }
        
                </tbody>

            </table>
        </div>
    )
}

export default ListTodo;
