import 'bootstrap/dist/css/bootstrap.min.css';
import React, {Fragment, useEffect, useState} from "react";


const ListTodo = () =>
{
    const [todos, setTodos] = useState([]);

    //delete todos
    const deleteTodos = async (id) =>
    {
        try {
            const response = await fetch("http://localhost:5000/todos/${id}",
            {
                method : "DELETE"
            })
            const restData = todos.filter((currentTodo) => { if(id != currentTodo.todo_id )
            {
                return currentTodo;
            }})
            setTodos(restData);


        } catch (err) {
            console.log(err);
        }
    }
    const getTodos = async () =>
    {
        try {
            const response = await fetch("http://localhost:5000/todos");
            const jsonData = await response.json();
            setTodos(jsonData.rows);
            
        } catch (err) {
            console.log(err)
        }

    }

    useEffect(() => {
        getTodos();

    }, []);
    
    return (
        <div className="container">
            <table class="table">
                <thead>
                    <tr>
                        <th>Description</th>              
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    todos.map((elementObject) => {return (
                        <tr>
                            <td>{elementObject.description}</td>
                            <td>1</td>
                            <td>
                                {/* <button onClick={() => deleteTodos(elementObject.todo_id)}>x</button> */}
                            </td>
                        </tr>
                    )})  
                    }
        
                </tbody>

            </table>
        </div>
    )
}

export default ListTodo;
